import React from 'react';

const GameControls = props => {
    const {
        generateEmptyGrid,
        runSimulation,
        clearGrid,
        setGrid,
        running,
        setRunning,
        runningRef,
        numRows,
        setNumRows,
        generation,
        setGeneration,
        speed,
        setSpeed,
    } = props;

    const increaseSize = () => {
        if (numRows < 40) setNumRows(numRows + 5)
        setGeneration(0)
    }

    const decreaseSize = () => {
        if (numRows > 20) setNumRows(numRows - 5)
        setGeneration(0)
    }

    const slower = () => {
        if (speed < 2000) setSpeed(speed +250)
    }

    const faster = () => {
        if (speed > 250) setSpeed(speed - 250)
    }

    return (
        <div className="game-controls">
            <button onClick={slower}></button>
            <button onClick={faster}></button>
            <button onClick={increaseSize}></button>
            <button onClick={slower}></button>
        </div>
    )
}

export default GameControls;
