import React from 'react';

const GameControls = props => {
    const {
        generateEmptyGrid,
        runSimulation,
        clearGrid,
        setGrid,
        running,
        setRunning,
        runningRef,
        numRows,
        setNumRows,
        generation,
        setGeneration,
        speed,
        setSpeed,
    } = props;

    const increaseSize = () => {
        if (numRows < 40) setNumRows(numRows + 5)
        setGeneration(0)
    }

    const decreaseSize = () => {
        if (numRows > 20) setNumRows(numRows - 5)
        setGeneration(0)
    }

    const slower = () => {
        if (speed < 2000) setSpeed(speed +250)
    }

    const faster = () => {
        if (speed > 250) setSpeed(speed - 250)
    }

    return (
        <div className="game-controls">
            <button 
                onClick={() => { 
                    setRunning(!running);
                    if (!running) {
                    runningRef.current = true;
                    runSimulation()
                    }
                }}
            >
                {running ? 'Stop' : 'Start' }
            </button>

            <button onClick={() => clearGrid()}>Clear Grid</button>

            <button onClick={slower}>Slower</button>
            <button onClick={faster}>Faster</button>
            <button disabled={running} onClick={decreaseSize}>Decrease Grid Size</button>
            <button disabled={running} onClick={increaseSize}>Increase Grid Size</button>

            <button
                onClick={() => {
                const rows = [];
                    for (let i = 0; i < numRows; i++) {
                        rows.push(Array.from(Array(numRows), () => (Math.random() > 0.7 ? 1 : 0)));
                    }
                setGrid(rows);
            }}
            > 
                Randomize!
            </button>
        </div>
    )
}

export default GameControls;
