import React from 'react';
import produce from 'immer';


const numRows = 25;
const numCols = 25;


const Grid = ({ grid, SetGrid}) => {

    const activateColor = (rowIndex, colInex) => {
        SetGrid(g => {
            return produce(g, gridCopy => {
                gridCopy[rowIndex][colIndex] ? gridCopy[rowIndex][colIndex] = 0 : gridCopy[rowIndex][colIndex] = 1;
            })
        })
    }

    return (
        <div 
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${numCols}, 20px)`
        }}
        >
        {grid.map((row, i1) => 
          row.map((col, k) => (
            <div
            key={`${i}-${k}`}
            onClick={() => {
              const newGrid = produce(grid, gridCopy => {
                gridCopy[i][k] = grid[i][k] ? 0 : 1;
              });
              setGrid(newGrid);
            }}
            style ={{ 
              width: 20, 
              height: 20, 
              backgroundColor: grid[i][k] ? "pink" : undefined, 
              border: "solid 1px black"
            }} 
            />
            ))
            )}
      </div>
    )
}

export default Grid;
