      // })
      setGrid(() => {
        const gridCopy = grid.map(row => [...row]);

      })
    }

    return (
      <div className="grid">
        {grid.map((row, i1) => {
          return <div key={'row' + i1} className="row">
              {row.map((col, i2) => {
                return (
                  <div 
                    key={'col' + i2}
                    className={col ? "cell alive" : "cell"} 
                    onClick={running ? null : () => activateColor(i1, i2)} 
                  />)
            })}
          </div>
        })}
      </div>
    );
  }

export default Grid;
