import React from 'react';

const GameControls = ({ setRunning, running, runningRef, runSimulation, setGrid, generateEmptyGrid, numRows }) => {
    return (
        <div className="game-controls">
            <button 
                onClick={() => { 
                    setRunning(!running);
                    if (!running) {
                    runningRef.current = true;
                    runSimulation();
                    }
                }}
            >
                {running ? 'Stop' : 'Start' }
            </button>

            <button
                onClick={() => {
                const rows = [];
                    for (let i = 0; i < numRows; i++) {
                        rows.push(Array.from(Array(numRows), () => (Math.random() > 0.7 ? 1 : 0)));
                    }
                setGrid(rows);
            }}
            > 
                Randomize!
            </button>

            <button
                onClick={() => {
                    setGrid(generateEmptyGrid());
                }}
            > 
                Clear Grid
            </button>
        </div>
    )
}

export default GameControls;
